import "./App.css";
import React, { useState, useEffect } from "react";
import { sortBy } from "lodash";
import vinylData from "./assets/vinyldata.json";
import Vinyl from "./components/vinyl";
import Cart from "./components/cart";
import FilterButtons from "./components/FilterButtons";
import SortingButton from "./components/SortingButton";

/* ####### DO NOT TOUCH -- this makes the image URLs work ####### */
vinylData.forEach((vinyl) => {
  vinyl.image = process.env.PUBLIC_URL + "/" + vinyl.image;
});
/* ############################################################## */

function App() {
  // TODO: use useState to create a state variable to hold the state of the cart
  /* add your cart state code here */
  const [cart, updateCart] = useState([]);
  // const [setVinyls] = useState(vinylData);
  const [filteredVinyls, setFilteredVinyls] = useState(vinylData);
  const [cartPrice, updateCartPrice] = useState(0.0);
  const [selectedGenre, setSelectedGenre] = useState("All");
  const [selectedPrice, setSelectedPrice] = useState("All");

  useEffect(() => {
    filterVinyls();
  }, [selectedGenre, selectedPrice]);

  // useEffect(() => {
    // sortVinyls(filteredVinyls);
  // }, [filteredVinyls]);

  const handleGenreChange = (genre) => {
    setSelectedGenre(genre);
    if (selectedGenre !== "All") {
      filteredVinyls = filteredVinyls.filter(
        (vinyl) => vinyl.genre === selectedGenre
      );
    }
    return filteredVinyls;
  };

  const handlePriceChange = (price) => {
    setSelectedPrice(price);
    if (selectedPrice !== "All") {
      const [minPrice, maxPrice] = selectedPrice.split("-").map(Number);
      filteredVinyls = filteredVinyls.filter(
        (vinyl) => vinyl.price >= minPrice && vinyl.price <= maxPrice
      );
    }
    return filteredVinyls;
  };

  const handleSortByPrice = () => {
    // const sortedResult = [...filteredVinyls].sort((a, b) => a.price - b.price);
    const sortedResult = sortBy(filteredVinyls, "price");
    setFilteredVinyls(sortedResult);
    return filteredVinyls;
  };

  const filterVinyls = () => {
    let filteredVinyls = vinylData;

    if (selectedGenre !== "All") {
      filteredVinyls = filteredVinyls.filter(
        (vinyl) => vinyl.genre === selectedGenre
      );
    }

    if (selectedPrice !== "All") {
      const [minPrice, maxPrice] = selectedPrice.split("-").map(Number);
      filteredVinyls = filteredVinyls.filter(
        (vinyl) => vinyl.price >= minPrice && vinyl.price <= maxPrice
      );
    }
    // const sortedResult = [...filteredResult].sort((a, b) => a.price - b.price);

    // setFilteredVinyls(sortedResult);

    // sortVinyls(filteredResult);
    // setFilteredVinyls(filteredResult);
    return filteredVinyls;
  };

  return (
    <div>
      <FilterButtons
        handleGenreChange={handleGenreChange}
        handlePriceChange={handlePriceChange}
      />

      {/* Sorting button */}
      <SortingButton handleSortByPrice={handleSortByPrice} />

      {/* Display filtered books */}
      {/* Display sorted and filtered books */}
      <ul>
        {filteredVinyls.map((Vinyl) => (
          <li key={Vinyl.name}>
            {Vinyl.genre} - ${Vinyl.price}{" "}
          </li>
        ))}
      </ul>
      {/* {filterVinyls().map((vinyl) => (
        <Vinyl
          // <li key={item.name}>{item.genre} - ${item.price} </li>
          name={vinyl.name}
          genre={vinyl.genre}
          price={vinyl.price}
          image={vinyl.image}
          updateCart={updateCart}
          cart={cart}
          updatePrice={updateCartPrice}
          currPrice={cartPrice}
        />
      ))} */}
      <div>
        <h2>Cart</h2>
        {
          /* TODO: render a list of items in the cart */
          <Cart cartItems={cart} cartPrice={cartPrice} curPrice={cartPrice} />
        }
      </div>
    </div>
  );
}

