import "./App.css";
import { useState } from "react";
import bakeryData from "./assets/vinyldata.json";
import Vinyl from "./components/vinyl";
import Cart from "./components/cart";

/* ####### DO NOT TOUCH -- this makes the image URLs work ####### */
bakeryData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});
/* ############################################################## */

function App() {
  // TODO: use useState to create a state variable to hold the state of the cart
  /* add your cart state code here */
  const [cart, updateCart] = useState([]);
    const [cartPrice, updateCartPrice] = useState(0.0);




  return (
    <div className="App">
      <h1>My Bakery</h1> {/* TODO: personalize your bakery (if you want) */}
      {vinylData.map(
        (
          item,
          // index // TODO: map bakeryData to BakeryItem components
        ) => (
          <Vinyl  // replace with BakeryItem component
          name={item.name}
          price={item.price}
          description={item.description}
          image={item.image}
          updateCart={updateCart}
          cart={cart}
          updatePrice={updateCartPrice}
          currPrice={cartPrice}
          />
        )
      )}
      <div>
        <h2>Cart</h2>
        {
          /* TODO: render a list of items in the cart */
          <Cart cartItems={cart} cartPrice={cartPrice} curPrice={cartPrice}/>
        }
      </div>
    </div>
  );
}

export default App;
